// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.

answer = ifScript();

function ifScript() {
	if (workflow.scratchpad.srvclass == 'cmdb_ci_win_server') {
		return 'yes';
	}
		return 'no';
}

// Determine if the CI on the remediation task is an application, and
//   if yes, find the server CI it is running on.  Otherwise, use server CI

var ciclass = current.alert.cmdb_ci.sys_class_name;

// These will need to be updated if a different crednetial alias is used
workflow.scratchpad.winAlias = 'x_snc_orch_em_flow.orch_em_winAlias';
workflow.scratchpad.lnxAlias = 'x_snc_orch_em_flow.orch_em_lnxAlias';

if ((ciclass == 'cmdb_ci_win_server') || (ciclass == 'cmdb_ci_linux_server')){
	workflow.scratchpad.srvclass = ciclass;
	workflow.scratchpad.targetip = current.alert.cmdb_ci.ip_address;
} else {
	// Assume that a non-server CI is an application that runs on a server
	var relGr = new GlideRecord('cmdb_rel_type');
	relGr.addQuery('name', 'Runs on::Runs');
	relGr.query();
	
	if (relGr.next()) {
		// Find the runs-on relationship for the app CI
		var gr = new GlideRecord('cmdb_rel_ci');
		gr.addQuery('parent', current.alert.cmdb_ci);
		gr.addQuery('type', relGr.sys_id);
		gr.query();
		
		if(gr.next()){
			// The child of the runs-on relationship should be the server CI
			var cldGr = new GlideRecord('cmdb_ci_server');
			cldGr.addQuery('sys_id', gr.child);
			cldGr.query();
			
			if(cldGr.next()) {
				workflow.scratchpad.srvclass = cldGr.sys_class_name;
				workflow.scratchpad.targetip = cldGr.ip_address;
			}
		}
	}
}
gs.info('---> winAlias: ' + workflow.scratchpad.winAlias);
gs.info('---> lnxAlias: ' + workflow.scratchpad.lnxAlias);
gs.info('---> class: ' + workflow.scratchpad.svrclass);
gs.info('---> targetIP: ' + workflow.scratchpad.targetip);

